import React, { Fragment } from "react";
import { useParams, Route, Link, useRouteMatch } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
const DUMMY_QUOTES = [
  { id: "q1", author: "ahmed", text: "learning react in fun" },
  { id: "q2", author: "ali", text: "learning react in fun" },
  { id: "q3", author: "samer", text: "learning react in fun" },
  { id: "q4", author: "ammar", text: "learning react in fun" },
  { id: "q5", author: "ammar", text: "learning react in fun" },
];
const QuoteDetail = () => {
  const match = useRouteMatch();
  const params = useParams();
  console.log(match);
  const quote = DUMMY_QUOTES.find((quote) => quote.id == params.quoteId);
  if (!quote) {
    return <p>no quote found</p>;
  }
  return (
    <Fragment>
      <h1>QuoteDetail</h1>
      <HighlightedQuote author={quote.author} text={quote.text} />
      <Route path={match.path} exact>
        <div className="centered">
          <Link className="btn--flat" to={`${match.url}/comments`}>
            Load Comments
          </Link>
        </div>
      </Route>

      <Route path={`${match.path}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuoteDetail;
